export default (posts = [], action) => {
    switch (action.type) {
        case FETCH_ALL:
            return action.payload;
        case LIKE:
            return posts.map((post) => (post._id === action))
        case CREATE:
            return [ ...posts, action.payload];
        default:
            return posts;
    }
}
